import styled from "styled-components";


export const SignInSignUpPageContainer = styled.div`
    width: 850px;
    display: flex;
    justify-content: space-between;
    margin: 30px auto;

    @media screen and (max-width: 800px){
    display: flex;
    flex-direction: column;
    width: unset;
    align-items: center;
    }
`
