import {takeLatest, put, all, call} from "redux-saga/effects";
import  UserActionTypes  from "./userTypes";
import { auth, googleProvider } from "../../firebase/firebase.utils";
import { googleSignInFailure } from "./userActions";



function* signInWithGoogle(){
    try {
        const userRef = yield auth.signInWithPopup(googleProvider);
        console.log(userRef);

    } catch (error) {
        yield put(googleSignInFailure(error.message))
    }    
}

export function* onGoogleSignInStart(){
    yield takeLatest(UserActionTypes.GOOGLE_SIGNI_IN_START, signInWithGoogle)
}

export function* userSagas(){
    yield all([call(onGoogleSignInStart)])
}
